import React, { useState , useReducer, useEffect } from 'react';
import Input from '../Input/Input';
import Card from '../UI/Card/Card';
import classes from './Login.module.css';




const nameFunction = (state,action) => {
  if (action.type === "NAME_INPUT" ){
    return {nameValue:action.val, nameIsValid:action.val.trim().length > 1};
  }
  if (action.type === "NAME_BLUR"){
    return {nameValue:state.nameValue, nameIsValid:state.nameValue.trim().length > 1};
  }

  return {nameValue:"", nameIsValid:false}
}
const emailFunction = (state,action) => {
  if( action.type ==="PASSWORD_INPUT" ){
    return {passwordValue:action.val, passwordIsValid:action.val.trim().length > 6};
  }
  if ( action.type ==="Blur_Password" ){
    return {passwordValue:state.passwordValue, passwordIsValid:state.passwordValue.trim().length > 6}
  }
  return {emailValue:"", emailIsValid:false}
}
const passwordFunction = (state,action) => {
  if (action.type === "EMAIL_INPUT" ){
    console.log(state.emailValue)
    return {emailValue:action.val, emailIsValid:action.val.includes("@")};
  }
  if (action.type === "EMAIL_BLUR"){
    return {emailValue:state.emailValue, emailIsValid:state.emailValue.includes("@")};
  }
  return { passwordValue:"", passwordIsValid:false}
}




const Login = (props) => {
  const [isLogin, setIsLogin] = useState(true);
  const [formIsValid, setFormIsValid] = useState(false);


  const [nameState, dispatchName] = useReducer(nameFunction,{nameValue:"", nameIsValid:null})
  const [emailState, dispatchEmail] = useReducer(emailFunction,{emailValue:"", emailIsValid:null})
  const [passwordState, dispatchPassword] = useReducer(passwordFunction,{passwordValue:"", passwordIsValid:null})
  useEffect(()=>{
    const myTimeOut = setTimeout(() => {

      setFormIsValid(
        emailState.emailIsValid && passwordState.passwordIsValid 
      );
    },300)
    return ()=> {
      console.log("Clear TimeOut Called");
      clearTimeout(myTimeOut)
    }

  },[emailState.emailIsValid, passwordState.passwordIsValid])

  const nameChangeHandler = (event) => {
    dispatchName({type:"NAME_INPUT", val:event.target.value});
  };
  const emailChangeHandler = (event) => {
    dispatchEmail({type:"EMAIL_INPUT", val:event.target.value});
    console.log(emailState.emailValue)
  };

  const passwordChangeHandler = (event) => {
    dispatchPassword({type:"PASSWORD_INPUT", val:event.target.value})
  };
  const validateNameHandler = () => {
    dispatchName({type:"Name_BLUR"});
  };

  const validateEmailHandler = () => {
    dispatchEmail({type:"EMAIL_BLUR"});
  };

  const validatePasswordHandler = () => {
    dispatchPassword({type:"Blur_Password"})

  };

  const submitHandler = (event) => {
    event.preventDefault();
    props.onLogin(emailState.emailValue, passwordState.passwordValue);
  };
  const switchAuthModeHandler = () => {
    setIsLogin((prevState) => !prevState);
  };

  const registerUser = () => {

  }
  return (
    <Card className={classes.login}>
      <form onSubmit={submitHandler}>
        {!isLogin &&         <Input 
          label = "Name"
          type = "name"
          id = "name"
          value = {nameState.nameValue}
          onChange={nameChangeHandler}
          onBlur={validateNameHandler}
          isValid = {nameState.nameIsValid}
        />}
        <Input 
          label = "Email"
          type = "email"
          id = "email"
          value = {emailState.emailValue}
          onChange={emailChangeHandler}
          onBlur={validateEmailHandler}
          isValid = {emailState.emailIsValid}
        />
        <Input 
          label = "Password"
          type = "password"
          id = "password"
          value = {passwordState.passwordValue}
          onChange={passwordChangeHandler}
          onBlur={validatePasswordHandler}
          isValid = {passwordState.passwordIsValid}
        />
        <div className={classes.actions}>
        <div className={classes.actions}>
         {isLogin ?  <button disabled={!formIsValid}>Login</button> :  <button onClick={registerUser} disabled={!formIsValid}>Create Account</button>}
          <button
            type='button'
            className={classes.toggle}
            onClick={switchAuthModeHandler}
          >
            {isLogin ? 'Create new account' : 'Login with existing account'}
          </button>
        </div>
        </div>
      </form>
    </Card>
  );
};

export default Login;
