import React, { useState , useReducer, useEffect } from 'react';
import Input from '../Input/Input';
import Card from '../UI/Card/Card';
import classes from './Login.module.css';




const formFunction = (state,action) => {
  if (action.type === "EMAIL_INPUT" ){
    return {emailValue:action.val, emailIsValid:action.val.includes("@")};
  }
  if (action.type === "INPUT_BLUR"){
    return {emailValue:state.value, emailIsValid:state.value.includes("@")};
  }
  if( action.type ==="PASSWORD_INPUT" ){
    return {passwordValue:action.val, passwordIsValid:action.val.trim().length > 6};
  }
  if ( action.type ==="Blur_Password" ){
    return {passwordValue:state.value, passwordIsValid:state.value.trim().length > 6}
  }
  return {emailValue:"", emailIsValid:false, passwordValue:"", passwordIsValid:false}
}



const Login = (props) => {
  const [isLogin, setIsLogin] = useState(true);
  const [formIsValid, setFormIsValid] = useState(false);


  const [formState, dispatchForm] = useReducer(formFunction,{emailValue:"", emailIsValid:null, passwordValue:"", passwordIsValid:null,})


  useEffect(()=>{
    const myTimeOut = setTimeout(() => {

      setFormIsValid(
        formState.emailIsValid && formState.passwordIsValid
      );
    },300)
    return ()=> {
      console.log("Clear TimeOut Called");
      clearTimeout(myTimeOut)
    }

  },[formState.emailIsValid, formState.passwordIsValid])

  const nameChangeHandler = (event) => {
    dispatchForm({type:"NAME_INPUT", val:event.target.value});
  };
  const emailChangeHandler = (event) => {
    dispatchForm({type:"EMAIL_INPUT", val:event.target.value});
  };

  const passwordChangeHandler = (event) => {
    dispatchForm({type:"PASSWORD_INPUT", val:event.target.value})
  };

  const validateEmailHandler = () => {
    dispatchForm({type:"INPUT_BLUR"});
  };

  const validatePasswordHandler = () => {
    dispatchForm({type:"Blur_Password"})

  };

  const submitHandler = (event) => {
    event.preventDefault();
    props.onLogin(formState.emailValue, formState.passwordValue);
  };
  const switchAuthModeHandler = () => {
    setIsLogin((prevState) => !prevState);
  };

  const registerUser = () => {

  }
  return (
    <Card className={classes.login}>
      <form onSubmit={submitHandler}>
        {!isLogin &&         <Input 
          label = "Name"
          type = "name"
          id = "name"
          value = {emailState.value}
          onChange={nameChangeHandler}
          onBlur={validateEmailHandler}
          isValid = {emailState.isValid}
        />}
        <Input 
          label = "Email"
          type = "email"
          id = "email"
          value = {formState.emailValue}
          onChange={emailChangeHandler}
          onBlur={validateEmailHandler}
          isValid = {formState.emailIsValid}
        />
        <Input 
          label = "Password"
          type = "password"
          id = "password"
          value = {formState.passwordValue}
          onChange={passwordChangeHandler}
          onBlur={validatePasswordHandler}
          isValid = {formState.passwordIsValid}
        />
        <div className={classes.actions}>
        {isLogin ? <div className={classes.actions}>
         {isLogin ?  <button disabled={!formIsValid}>Login</button> :  <button onClick={registerUser} disabled={!formIsValid}>Create Account</button>}
          <button
            type='button'
            className={classes.toggle}
            onClick={switchAuthModeHandler}
          >
            {isLogin ? 'Create new account' : 'Login with existing account'}
          </button>
        </div>:""}
        </div>
      </form>
    </Card>
  );
};

export default Login;
