import React, { useState , useReducer, useEffect } from 'react';

import Card from '../UI/Card/Card';
import classes from './Login.module.css';
import Button from '../UI/Button/Button';


const emailFunction = (state,action) => {
  if (action.type === "VALID_INPUT" ){
    return {value:action.val, isValid:action.val.includes("@")};
  }
  if (action.type === "INPUT_BLUR"){
    return {value:state.value, isValid:state.value.includes("@")};
  }
  return {value:"", isValid:false}
}

const passwordFunction = (state, action) =>{
  if( action.type ==="PASSWORD_INPUT" ){
    return {value:action.val, isValid:action.val.trim().length > 6};
  }
  if ( action.type ==="Blur_Password" ){
    return {value:state.value, isValid:state.value.trim().length > 6}
  }
 return {value:"", isValid:false}
}

const Login = (props) => {
  // const [enteredEmail, setEnteredEmail] = useState('');
  // const [emailIsValid, setEmailIsValid] = useState();
  // const [enteredPassword, setEnteredPassword] = useState('');
  // const [passwordIsValid, setPasswordIsValid] = useState();
  const [formIsValid, setFormIsValid] = useState(false);

  // useReducer
  const [emailState, dispatchEmail] = useReducer(emailFunction,{value:"", isValid:null})
  const [passwordState, dispatchPassword] = useReducer(passwordFunction,{value:"", isValid:null})

  // useEffect we effective when ever the dependencies changes like in the below case when ever the enter email or password changes the use effect will called!

  useEffect(()=>{
    const myTimeOut = setTimeout(() => {
      console.log("TimeOut Called");
      setFormIsValid(
        emailState.value.includes('@') && passwordState.value.trim().length > 6
      );
    },300)
// to avoid our data lost when we sent it htrough http thats why we add the timeOut so after we done writing a word or when we pause it 
// will then sent the data to authenticate as a whole instead of everykeystrokecand CleartimeOut clears the data when everr a keystroke
// is done so that the timers starts from the zero 

// below is cleanup function
    return ()=> {
      console.log("Clear TimeOut Called");
      clearTimeout(myTimeOut)
    }

  },[emailState.value , passwordState.value])


  const emailChangeHandler = (event) => {
    dispatchEmail({type:"VALID_INPUT", val:event.target.value});
    // setFormIsValid(
    //   event.target.value && enteredPassword.trim().length > 6
    //       );

  };

  const passwordChangeHandler = (event) => {
    dispatchPassword({type:"PASSWORD_INPUT", val:event.target.value})
    // setFormIsValid(
    //   emailState.isValid && event.target.value.trim().length > 6
    //         );
  };

  const validateEmailHandler = () => {
    dispatchEmail({type:"INPUT_BLUR"});
    // console.log(emailState.isValid);
    // console.log(formIsValid );
  };

  const validatePasswordHandler = () => {
    dispatchPassword({type:"Blur_Password"})
    // setPasswordIsValid(enteredPassword.trim().length > 6);
    // console.log(passwordIsValid);
  };

  const submitHandler = (event) => {
    event.preventDefault();
    props.onLogin(emailState.value, passwordState.value);
  };

  return (
    <Card className={classes.login}>
      <form onSubmit={submitHandler}>
        <div
          className={`${classes.control} ${
            emailState.isValid === false ? classes.invalid : ''
          }`}
        >
          <label htmlFor="email">E-Mail</label>
          <input
            type="email"
            id="email"
            value={emailState.value}
            onChange={emailChangeHandler}
            onBlur={validateEmailHandler}
          />
        </div>
        <div
          className={`${classes.control} ${
            passwordState.isValid === false ? classes.invalid : ''
          }`}
        >
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={passwordState.value}
            onChange={passwordChangeHandler}
            onBlur={validatePasswordHandler}
          />
        </div>
        <div className={classes.actions}>
          <Button type="submit" className={classes.btn} disabled={!formIsValid}>
            Login
          </Button>
        </div>
      </form>
    </Card>
  );
};

export default Login;
